
function aug(Renderer) {
    function NewRenderer() {
        Renderer.apply(this, arguments);
    }

    NewRenderer.prototype           = new Renderer;
    NewRenderer.prototype.code      = aug.code;
    NewRenderer.prototype.codespan  = aug.codespan;

    return NewRenderer;
}

aug.code      = require("./renderers/code");
aug.codespan  = require("./renderers/codespan");

module.exports = aug;